import { useState } from "react"
const Task = () => {
    const [tasks, setTask]= useState([
        {
            id:1,
            text:'Doctors Appointment',
            day:'Feb 5th at 2:30pm',
            reminder:true,

        },
        {
            id: 2,
            text:'Meeting at school',
            day:'Feb 6th at 1:30pm',
            reminder:true,

        },
        {
            id: 3,
            text:'Food Shopping',
            day:'Feb 5th at 2:30pm',
            reminder:true,

        },

    ])
    return (
        <>
            {tasks.map((task) => (
                                <h3>{task.text}</h3>
            ))}
        </>
    )
}

export default Task
